import React from 'react';
import PubNub from 'pubnub';

export interface PubNubContextValue {
  client: PubNub;
}

export const PubNubContext = React.createContext<PubNubContextValue | null>(
  null
);
import React from 'react';
import PubNub from 'pubnub';
import invariant from 'ts-invariant';

import { PubNubContext } from './PubNubContext';

export interface PubNubProviderProps<PubNubInstance> {
  client: PubNubInstance;
  children: React.ReactNode | React.ReactNode[] | null;
}

function appendPnsdk(pubnub: any) {
  if (typeof pubnub._addPnsdkSuffix === 'function') {
    pubnub._addPnsdkSuffix('react', 'React/__VERSION__');
  }
}

export const PubNubProvider: React.FC<PubNubProviderProps<PubNub>> = ({
  client,
  children,
}) => {
  const contextValue = React.useMemo(() => {
    return { client };
  }, [client]);

  invariant(
    contextValue.client,
    'PubNubProvider was not passed a client instance. Make ' +
      'sure you pass in your client via the "client" prop.'
  );

  React.useEffect(() => {
    appendPnsdk(contextValue.client);
  }, []);

  return (
    <PubNubContext.Provider value={contextValue}>
      {children}
    </PubNubContext.Provider>
  );
};
import React from 'react';
import PubNub from 'pubnub';
import invariant from 'ts-invariant';

import { PubNubContext } from './PubNubContext';

export interface PubNubConsumerProps {
  children: (client: PubNub) => React.ReactChild | null;
}

export const PubNubConsumer: React.FC<PubNubConsumerProps> = ({ children }) => {
  const context = React.useContext(PubNubContext);

  invariant(
    context && context.client,
    'Could not find "client" in the context of PubNubConsumer. ' +
      'Wrap the root component in an <PubNubProvider>.'
  );

  return <>{children(context!.client)}</>;
};
import { useContext } from 'react';
import PubNub from 'pubnub';
import invariant from 'ts-invariant';

import { PubNubContext } from '../context/PubNubContext';

export function usePubNub(): PubNub {
  const context = useContext(PubNubContext);

  invariant(
    context && context.client,
    'No PubNub Client instance can be found. Please ensure that you ' +
      'have called `PubNubProvider` higher up in your tree.'
  );

  return context!.client;
}
