import React, {Component} from 'react';
import LatestItem from './LatestItem';

import "../App.css"

import ScrollToBottom from 'react-scroll-to-bottom';

const styling = {
    width: "380px",
    height: "400px"
}

export default class Latest extends Component{
    constructor(props){
        super(props);

        this.itemClicked = this.itemClicked.bind(this);
    }

    state = {
        latest: new Set()
    }

    componentDidMount(){
        const ourSet = new Set()

        this.props.latest.forEach(latestItem => {
            if(!ourSet.has(latestItem)){
                ourSet.add(latestItem)
            }
        })

        this.setState({
            latest: ourSet
        })
    }

    itemClicked(e, item){
        e.preventDefault();
        this.props.latestClicked(item);
    }

    render(){
        return (
            <div className="laser-latest-div laser-shadow">
                <h4 className="text-center laser-black-text">Latest Reports</h4>    
                <ScrollToBottom style={styling}>
                    {
                        this.state.latest.map(item => {
                            return  <LatestItem key={item._id} itemClicked={this.itemClicked} item={item}/>
                        })
                    }
                </ScrollToBottom>
            </div>
        );
    }
}
